import Axios from "axios"
import AccountService from "./AccountService"

class ApiUtil {    
    createJwtToken(token) {
        return 'Bearer ' + token
    }

    setupInterceptors(token) {
        setupRequestInterceptor(token)
        setupResponseInterceptor()
    } 

    setupRequestInterceptor(token) {
        let header = this.createJwtToken(token)
        Axios.interceptors.request.use(
            (config) => {
                config.headers.authorization = header
                return config
            }
        )
    }

    setupResponseInterceptor() {
        console.log("Response Interceptor is being set up")
        const interceptor = Axios.interceptors.response.use(
            response => response,
            error =>
            {
                console.log("Error has occured")
                console.log(error.response)
                if (error.response.status !== 401) return Promise.reject(error);
                console.log("Error was 401")
                Axios.interceptors.response.eject(interceptor)

                AccountService.refreshToken()
                .then(
                    response => {
                        console.log(response)
                    }
                )
                .catch(
                    error => {
                        console.log(error.response)
                    }
                )
            }
        )
        console.log("Response Interceptor set up succesfully. ID of interceptor was " + interceptor)
    }
}

export default new ApiUtil()
