import React, {Component} from 'react'
import Button from 'react-bootstrap/Button'
import ReactDatePicker from 'react-datepicker'

import "react-datepicker/dist/react-datepicker.css";
import * as yup from 'yup'
import { Formik, Form, Field } from 'formik'

const signUpValidation = yup.object().shape({
    username: yup.string()
        .required('Username is required')
    ,password: yup.string()
        .min(8, 'Passwords needs to be longer than 8 characters')
        .matches(/[a-z]/, 'Password needs at least 1 lowercase character.')
        .matches(/[A-Z]/, 'Password needs at least 1 uppercase character.')
        .matches(/[1-9]/, 'Password needs at least a number.')
    /*,confirmPassword: yup.string()
        .required(),
    ,firstName: yup.string()
      .max(50, 'Too Long!')
      .required('Required')
    ,lastName: yup.string()
      .max(50, 'Too Long!')
      .required('Required')
    ,email: yup.string()
      .email('Invalid email')
      .required('Required')*/
  });

class SignUpPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: ''
        }
        this.onSubmit = this.onSubmit.bind(this)
    }

    render() {
        const startDate = new Date();
        let {username} = this.state
        return (
            <Formik
                initialStates={{username}}
                validationSchema={signUpValidation}
                onSubmit={this.onSubmit}
                validateOnChange={false}
                validateOnBlur={false}
                enableReinitialize={true}
            >
                {
                    (props) => 
                    {
                        return (
                            <Form>
                                <fieldset className="form-group">
                                    <label>Username</label>
                                    <Field className="form-control" type="text" name="username"/>
                                </fieldset>
                                <fieldset className="form-group">
                                    <label>Password</label>
                                    <Field className="form-control" type="text" name="password"/>
                                </fieldset>
                                <Button variant="primary" type="submit">Submit</Button>
                            </Form>
                        )
                    }
                }
            </Formik>            
        )
    }

    onSubmit(values, formikBag) {
        console.log(values)
        console.log(formikBag)
    }
}

export default SignUpPage
