import React, { Component } from 'react'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import { Link } from 'react-router-dom'
import { LinkContainer } from 'react-router-bootstrap'
import NavItem from 'react-bootstrap/NavItem'
import Button from 'react-bootstrap/Button'

class HeaderComponent extends Component {
    render() {
        return (
            <div className="App container">
                <Navbar fluid collapseOnSelect bg="dark" variant="dark">
                    <Navbar.Brand>
                        <Link to="/">Birthday Calendar</Link>
                    </Navbar.Brand>
                    <Navbar.Collapse className="justify-content-end">
                        <Nav pullRight>
                            {!this.props.isUserAuthenticated && 
                            <LinkContainer to='/signup'>
                                <NavItem>Get Started</NavItem>
                            </LinkContainer>}
                            {!this.props.isUserAuthenticated && <LinkContainer to='/login'><NavItem>Log In</NavItem></LinkContainer>}
                            {this.props.isUserAuthenticated && <Navbar.Text>Welcome, PlaceHolderFirstname</Navbar.Text>}
                            {this.props.isUserAuthenticated && <LinkContainer to='/logout'><NavItem>Log Out</NavItem></LinkContainer>}
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </div>
        )
    }
    
}

export default HeaderComponent
