import Axios from "axios"
import AccountService from "./AccountService"
import { LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY, SESSION_STORAGE_AUTHENTICATION_TOKEN_KEY } from '../Utility/GlobalConstants'

class ApiUtil {
    createJwtToken(token) {
        return 'Bearer ' + token
    }

    setupInterceptors(token, handler) {
        const requestInterceptor = this.setupRequestInterceptor(token)
        this.setupResponseInterceptor(token, handler, requestInterceptor)
    } 

    setupRequestInterceptor(token) {
        let header = this.createJwtToken(token)
        const interceptor = Axios.interceptors.request.use(
            (config) => {
                config.headers.authorization = header
                return config
            }
        )
        return interceptor
    }

    setupResponseInterceptor(token, handler, requestInterceptor) {
        const interceptor = Axios.interceptors.response.use(
            response => response,
            error =>
            {
                console.log("Error has occured")
                console.log(error.response)
                if (error.response.status !== 401) return Promise.reject(error);
                console.log("Error was 401")
                Axios.interceptors.response.eject(interceptor)
                Axios.interceptors.request.eject(requestInterceptor)
                AccountService.refreshToken(token)
                .then(
                    response => {
                        console.log("I got to response")
                        this.setupInterceptors(response.data.token)
                        if (localStorage.getItem(LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY))  localStorage.setItem(LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY, response.data.token)
                        sessionStorage.setItem(SESSION_STORAGE_AUTHENTICATION_TOKEN_KEY, response.data.token)
                    }
                )
                .catch(
                    error => {
                        console.log("error again")
                        sessionStorage.clear()
                        localStorage.clear()
                        console.log(requestInterceptor)
                        handler()                        
                        return Promise.reject(error);
                    }
                )
            }
        )
        return interceptor
    }
}

export default new ApiUtil()
