import axios from 'axios'

const ACCOUNT_SERVICE_API_HEADER = 'http://localhost:8080/users'
const API_HEADER = 'http://localhost:8080/'

class AccountService {
    signUp(user) {
        return axios.post(`${ACCOUNT_SERVICE_API_HEADER}/signup`, user)
    }

    logIn(credentials) {
        return axios.post(`${API_HEADER}/authenticate`, credentials)
    }

    checkExistingEmail(email) {
        return axios.post(`${ACCOUNT_SERVICE_API_HEADER}/checkEmail/${email}`)
    }
}

export default new AccountService()
