import React, {Component} from 'react'
import Utility from '../../Utility/Utility'

const defaultTimeBeforeRedirectionInSeconds = 5

class LogOutPage extends Component {
    constructor(props){
        super(props)
        this.state = {
            timeLeft : defaultTimeBeforeRedirectionInSeconds
        }
    }

    componentDidMount() {
        if (Utility.isUserLoggedIn())
        {
            sessionStorage.clear()
            var timer = setInterval(
                () => 
                {
                    this.setState({timeLeft: this.state.timeLeft - 1})
                    if (this.state.timeLeft <= 0) {
                        clearInterval(timer)
                        this.props.history.push('/login')
                    }
                }
            , 1000)
        }        
    }

    render() {
        let isUserLoggedIn = Utility.isUserLoggedIn()
        return (
            <div>
                {isUserLoggedIn ?(
                    <div>
                        <div className='alert alert-success'>
                            You have logged out
                        </div>
                        <div> You will be automatically redirected to Log In page in {this.state.timeLeft} seconds.</div>
                        <div> If not, please click <a href='/login'>here</a></div>
                    </div>
                ):(this.props.history.push('/login'))
            }
            </div>
            
        )
    }
}

export default LogOutPage
