class ApiUtil {    
    setupRequestInterceptor(token) {
        header = createJwtToken(token)
        axios.interceptors.request.use(
            (config) => {
                config.headers.authorization = header
                return config
            }
        )
    }

    createJwtToken(token) {
        return 'Bearer ' + token
    }
}

export default new ApiUtil
