import React, { Component } from 'react'
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import LogInComponent from './Pages/LogInComponent'
import HeaderComponent from './HeaderComponent'
import HomePage from './Pages/HomePage'
import SignUpPage from './Pages/SignUpPage'
import SuccessfulRegistrationPage from './Pages/SuccessfulRegistrationPage'
import LogOutPage from './Pages/LogOutPage'
import Utility from '../Utility/Utility'
import ApiUtil from '../ApiServices/ApiUtil'
import { LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY, SESSION_STORAGE_AUTHENTICATION_TOKEN_KEY } from '../Utility/GlobalConstants'
import BirthdayPage from './Pages/BirthdayPage'
//import TestPage from './Pages/TestPage'

class BirthdayCalendarApp extends Component {
    constructor(props) {
        super(props)
        console.log("Constructor is called")
        this.state = {
            isUserLoggedIn: Utility.isUserAuthenticated()
        }

        this.initializeInterceptor = this.initializeInterceptor.bind(this)
        this.handleChange = this.handleChange.bind(this)

        this.initializeInterceptor()
    }

    initializeInterceptor() {
        var token = sessionStorage.getItem(SESSION_STORAGE_AUTHENTICATION_TOKEN_KEY) 
        if (!token && Utility.isUserAuthenticatedAndRemembered())
        {
            token = localStorage.getItem(LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY)
            sessionStorage.setItem(SESSION_STORAGE_AUTHENTICATION_TOKEN_KEY, token)
        }
        if (token) {
            ApiUtil.setupInterceptors(token, this.handleChange)
        }
    }

    handleChange() {
        this.setState({isUserLoggedIn: Utility.isUserAuthenticated()})
        console.log("Authentication change was handled")
    }

    render() {
        console.log("App is being rendered")
        return(
            <React.StrictMode>
                <div className="BirthdayApp App">
                    <Router>
                        <>
                            <HeaderComponent isUserLoggedIn={this.state.isUserLoggedIn}/>
                            <Switch>
                                <Route path="/" exact component = {HomePage}/>
                                <Route path="/login" render={(props) => <LogInComponent {...props} handler={this.handleChange} />} />
                                <Route path="/signup" component = {SignUpPage}/>
                                <Route path="/successfulRegistration" component = {SuccessfulRegistrationPage}/>
                                <Route path="/logout" render={(props) => <LogOutPage {...props} handler={this.handleChange} />} />
                                <Route path="/birthday/:id" component = {BirthdayPage}/>
                                <Route path="/birthday" component = {BirthdayPage}/>
                            </Switch>
                        </>
                    </Router>
                </div>
            </React.StrictMode>
        )    
    }        
}

export default BirthdayCalendarApp
