import React, {Component} from 'react'
import Button from 'react-bootstrap/Button'
//import ReactDatePicker from 'react-datepicker'

//import "react-datepicker/dist/react-datepicker.css";
import * as yup from 'yup'
import { Formik, Form, Field } from 'formik'

const signUpValidation = yup.object().shape({
    firstName: yup.string()
      .required('First Name is required'),
    lastName: yup.string()
      .required('Last Name is required'),
    email: yup.string()
      .email('Invalid email')
      .required(),
    password: yup.string()
        .min(8, 'Passwords has to be at least 8 characters long')
        .required(),    
    confirmPassword: yup.string()
        .oneOf([yup.ref('password')], 'Passwords must match')
        .required()
  });

class SignUpPage extends Component {
    constructor(props) {
        super(props)

        this.onSubmit = this.onSubmit.bind(this)
    }

    render() {
        let initialState = {
            email: '',
            password: '',
            confirmPassword: '',
            firstName: '',
            lastName: ''
        }
        return (
            <div className="container">
                <Formik
                    initialValues={initialState}
                    validationSchema={signUpValidation}
                    onSubmit={this.onSubmit}
                    validateOnChange={true}
                >
                    {
                        (props) =>
                        {
                            let {errors, touched} = props
                            return (
                                // Formik's <Form> is not compatible with React Bootstrap's <Form>. The entire section is therefore written using classic tags
                                <Form>
                                    <div className='row'>
                                        <div className='col'>
                                            <fieldset className="form-group">
                                                <label>First Name</label>
                                                <Field className="form-control" type="text" name="firstName"/>
                                                {errors.firstName && touched.firstName && <div>{errors.firstName}</div>}
                                            </fieldset>
                                        </div>
                                        <div className='col'>
                                        
                                        </div>
                                    </div>
                                    
                                    <fieldset className="form-group">
                                        <label>Last Name</label>
                                        <Field className="form-control" type="text" name="lastName"/>
                                        {errors.lastName && touched.lastName && <div>{errors.lastName}</div>}
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Email</label>
                                        <Field className="form-control" type="email" name="email"/>
                                        {errors.email && touched.email && <div>{errors.email}</div>}
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <label>Password</label>
                                        <Field className="form-control" type="password" name="password"/>
                                        {errors.password && touched.password && <div>{errors.password}</div>}
                                    </fieldset>
                                    <fieldset className="form-group">
                                        <div className="row">
                                            <div classname="col-3"><label>ConfirmPassword</label></div>
                                            <div classname="col-5"><Field className="form-control" type="password" name="confirmPassword"/></div>
                                            <div classname="col-2">{errors.confirmPassword && touched.confirmPassword && <div>{errors.confirmPassword}</div>}</div>
                                        </div>
                                       
                                    </fieldset>
                                    
                                    <Button variant="primary" type="submit">Submit</Button>
                                </Form>
                            )
                        }
                    }
                </Formik>
            </div>
            
        )
    }

    onSubmit(values) {
        console.log(values)
    }
}

export default SignUpPage
