import react, { Component } from "react";


const birthdayValidation = yup.object().shape({
    firstName: yup.string()
      .required('First Name is required'),
    lastName: yup.string()
      .required('Last Name is required'),
  });

class BirthdayPage extends Component {
    constructor(props) {
        super(props)

        this.onSubmit = this.onSubmit.bind(this)
    }

    render() {
        let initialState = {
            email: '',
            password: '',
            confirmPassword: '',
            firstName: '',
            lastName: ''
        }

        return(
            <div className="container">
                <Formik
                    initialValues={initialState}
                    validationSchema={birthdayValidation}
                    onSubmit={this.onSubmit}
                    validateOnChange={false}
                    validateOnBlur={true}
                >
                    {
                        (props) =>
                        {
                            let {errors, touched} = props
                            return (
                                // Formik's <Form> is not compatible with React Bootstrap's <Form>. The entire section is therefore written using classic tags
                                <Form>
                                    <div className='row'>
                                        <div className='col'>
                                            <fieldset className="form-group">
                                                <label>First Name</label>
                                                <Field className="form-control" type="text" name="firstName" placeholder="First Name"/>
                                                {(errors.lastName && touched.firstName)?(<div className="alert alert-danger">{errors.firstName}</div>):(<div> </div>)}
                                            </fieldset>
                                        </div>
                                        <div className='col'>
                                            <fieldset className="form-group">
                                                <label>Last Name</label>
                                                <Field className="form-control" type="text" name="lastName"  placeholder="Last Name"/>
                                                {(errors.lastName && touched.lastName)?(<div className="alert alert-danger">{errors.lastName}</div>):(<div></div>)}
                                            </fieldset>
                                        </div>
                                    </div>
                                    <div className='row'>
                                        <div className='col'>
                                            <fieldset className="form-group">
                                                <label>First Name</label>
                                                <Field as="select" name="color">
                                                    <option value="red">Red</option>
                                                    <option value="green">Green</option>
                                                    <option value="blue">Blue</option>
                                                </Field>
                                            </fieldset>
                                        </div>
                                        <div className='col'>
                                            <fieldset className="form-group">
                                                <label>Last Name</label>
                                                <Field className="form-control" type="text" name="lastName"  placeholder="Last Name"/>
                                                {(errors.lastName && touched.lastName)?(<div className="alert alert-danger">{errors.lastName}</div>):(<div></div>)}
                                            </fieldset>
                                        </div>
                                    </div>
                                    <Button variant="primary" type="submit">Sign Up</Button>
                                </Form>
                            )
                        }
                    }
                </Formik>
            </div>
        )
    }

    onSubmit(values) {
        let {firstName, lastName, email, password} = values
        let user = {firstName, lastName, email, password}
        AccountService.signUp(user)
            .then(
                response => 
                {
                    this.props.history.push('/successfulRegistration')
                }
            )
            .catch(
                error => 
                {
                    console.log('failed')
                    console.log(error)
                }
            )
    }
}

export default BirthdayPage
