import axios from 'axios'

const ACCOUNT_SERVICE_API_HEADER = 'http://localhost:8080/users'

class AccountService {
    signUp(user) {
        let basicAuthHeader = 'Basic ' + window.btoa('Anima' + ':' + 'P@ssw0rd')
        console.log(basicAuthHeader)
        /*return axios.post(`${ACCOUNT_SERVICE_API_HEADER}/signup`, user, 
        {
            headers: {
                authorization:basicAuthHeader
            }
        })*/
        return axios.get(`${ACCOUNT_SERVICE_API_HEADER}/users`)
    }
}

export default new AccountService()
