import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import BirthdayService from "../../ApiServices/BirthdayService";
import Utility from "../../Utility/Utility";
import ApiUtil from "../../ApiServices/ApiUtil";
import { LOCAL_STORAGE_AUTHENTICATION_TOKEN_KEY } from "../../Utility/GlobalConstants";

class HomePage extends Component {
    constructor(props) {
        super(props)

        console.log(this.props)

        this.state = {
            birthdayList: []
        }
    }

    componentDidMount() {
        this.populateForm()
    }

    populateForm() {
        if (props.isUserLoggedIn()) {
            BirthdayService.getBirthdayList()
            .then (
                response => {
                    this.setState({birthdayList: response.data})
                }
            )
            .catch (
    
            )         
        }
        else {
            this.props.history.push('/login')
        }
    }

    render() {
        return (
            <div>
                <h1>List of Birthdays</h1>
                <div className="container">
                    <Table className="table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Birthday</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.birthdayList.map(birthday => 
                                    <tr key={birthday.id}>
                                    <td>{Utility.getFullNameFromComponents(birthday.firstName, birthday.lastName, birthday.middleName, birthday.suffix)}</td>
                                    <td>{Utility.getBirthdayFromComponents(birthday.birthmonth, birthday.birthday)}</td>
                                    <td><Button variant="primary">Details</Button></td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </Table>                    
                </div>
                <button className='btn-success'>Add a new Birthday</button>
            </div>
        )
    }
}

export default HomePage
