import React, {Component} from 'react'
import Form from 'react-bootstrap/Form'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import ReactDatePicker from 'react-datepicker'

import "react-datepicker/dist/react-datepicker.css";
import * as yup from 'yup'
import { Formik } from 'formik'

const signUpValidation = yup.object().shape({
    username: yup.string()
        .required('Username is required'),
    password: yup.string()
        .min(8, 'Passwords needs to be longer than 8 characters')
        .matches(/[a-z]/, 'Password needs at least 1 lowercase character.')
        .matches(/[A-Z]/, 'Password needs at least 1 uppercase character.')
        .matches(/[1-9]/, 'Password needs at least a number.'),
    confirmPassword: yup.string()
        .required(),
    firstName: yup.string()
      .max(50, 'Too Long!')
      .required('Required'),
    lastName: yup.string()
      .max(50, 'Too Long!')
      .required('Required'),
    email: yup.string()
      .email('Invalid email')
      .required('Required'),
  });

class SignUpPage extends Component {
    constructor(props) {
        super(props)
        
        this.onSubmit = this.onSubmit.bind(this)
    }

    render() {
        const startDate = new Date();
        return (
            <div>
                <h1>Todo</h1>
                <div className="container">
                    <Formik 
                        onSubmit={this.onSubmit}
                        validateOnChange={false}
                        validateOnBlur={false}
                        enableReinitialize={true}
                    >
                        {
                            (props) => (
                                <Form>
                                    <button className="btn-success" type="submit">Save</button>
                                </Form>
                            )
                        }
                    </Formik>
                </div>
            </div>           
        )
    }

    onSubmit() {
        alert("Test")
    }
}

export default SignUpPage
