import React, {Component} from 'react'
import { Formik, Form, Field } from 'formik'
import * as yup from 'yup'
import AccountService from '../../ApiServices/AccountService'
import ApiUtil from '../../ApiServices/ApiUtil'
import Utility from '../../Utility/Utility'
//import GoogleBtn from '../Buttons/GoogleButton'

const logInValidation = yup.object().shape({
    email: yup.string()
      .email('Invalid email')
      .required("Email is required"),
    password: yup.string()
        .required("Password is required")
})
class LogInComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: '',
            password: ''
        }

        this.logIn = this.logIn.bind(this)
    }

    render() {
        let {email, password} = this.state
        return (
            <div className="container logInPage">
                <Formik
                    initialValues={{email, password}}
                    onSubmit={this.logIn}
                    validationSchema={logInValidation}
                >
                {
                    (props) => 
                    {
                        let {errors, touched} = props
                        return (
                            <div>
                                <h1>Log In to Birthday Calendar</h1>
                                <Form>                                
                                    <fieldset className="form-group">
                                        <Field type="email" name="email" placeholder="Email"/>
                                    </fieldset>          
                                    <fieldset className="form-group">
                                        <Field type="password" name="password" placeholder="Email"/>                                    
                                    </fieldset>
                                    <fieldset class="form-check">
                                        <input type="checkbox" id="rememberSignInChk"/>
                                        <label for="rememberSignInChk">&nbsp;Remember me on this device</label>
                                    </fieldset>
                                    <button className="btn-success" type="submit">Log In</button>
                                    {this.state.serverErrorMessage?(<div className="alert alert-danger">{this.state.serverErrorMessage}</div>):(<div></div>)}
                                    {(errors.email && touched.email)?(<div className="alert alert-danger">{errors.email}</div>):(<div></div>)}
                                    {(errors.password && touched.password)?(<div className="alert alert-danger">{errors.password}</div>):(<div></div>)}
                                </Form>
                            </div>                            
                        )
                        
                    }
                }
                </Formik>
            </div>
        )
    }

    logIn(logInCredentials) {
        this.setState({serverErrorMessage: null})
        AccountService.logIn(logInCredentials)
        .then(
            response => {
                ApiUtil.setupRequestInterceptor(response.data.token)
                this.props.history.push('/home')
            }
        )
        .catch (
            error => {
                Utility.defaultApiErrorHandling(error, this)
            }            
        )
    }

    
}

export default LogInComponent
