import React, { Component } from "react";
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import BirthdayService from "../../ApiServices/BirthdayService";

class HomePage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            birthdayList: [
                {name: "Anh Mai", relationship: "Mother", birthday: "08/06"},
                {name: "Duc Tran", relationship: "Brother", birthday: "12/30"}
            ]
        }
    }

    componentDidMount() {
        this.populateData()
    }

    populateData() {
        BirthdayService.getBirthdayList()
        .then (
            response => {
                this.setState({birthdayList: response.data})
            }
        )
        .catch (

        )
    }

    render() {
        return (
            <div>
                <h1>List of Birthdays</h1>
                <div className="container">
                    <Table className="table">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Birthday</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.birthdayList.map(birthday => 
                                    <tr>
                                    <td>{birthday.name}</td>
                                    <td>{birthday.relationship}</td>
                                    <td>{birthday.birthday}</td>
                                    <td><Button variant="primary">Details</Button></td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </Table>                    
                </div>
                <button className='btn-success'>Add a new Birthday</button>
            </div>
        )
    }
}

export default HomePage
