import React from 'react'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import Utility from '../Utility/Utility'
import { LOCAL_STORAGE_FIRST_NAME_KEY } from '../Utility/GlobalConstants'

function HeaderComponent() {
    const isUserLoggedIn = Utility.isUserLoggedIn()
    return (        
        <header>
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="/home">Birthday Calendar</Navbar.Brand>
                <Navbar.Collapse className="justify-content-end">
                    {!isUserLoggedIn && <Nav.Link href='/signup'>Get Started</Nav.Link>}
                    {!isUserLoggedIn && <Nav.Link href='/login'>Log In</Nav.Link>}
                    {isUserLoggedIn && <Navbar.Text>Welcome, {localStorage.getItem(LOCAL_STORAGE_FIRST_NAME_KEY)}</Navbar.Text>}
                    {isUserLoggedIn && <Nav.Link href='/logout'>Log Out</Nav.Link>}
                </Navbar.Collapse>
            </Navbar>
        </header>
    )
}

export default HeaderComponent
